import React, { useState } from 'react';
import Video from 'videos/video.mp4';
import {
    HeroContainer,
    HeroBg,
    VideoBg,
    HeroContent,
    HeroH1,
    HeroP,
    HeroBtnWrapper,
    BtnLinkHome,
    BtnLinkSing
} from './HeroElements';


const HeroSection = () => {
    const [hover, setHover] = useState(false)

    const onHover = () => {
        setHover(!hover)
    }

    return (
        <HeroContainer id='home'>
            <HeroBg>
                <VideoBg autoPlay loop muted src={Video} type='video/mp4' />
            </HeroBg>
            <HeroContent>
                <HeroH1>
                    3-SAT Instance Generator
                </HeroH1>
                <HeroP>
                    Sign up and generate problem instances for algorithm testing
                </HeroP>
                <HeroBtnWrapper>
                    <BtnLinkHome to='/register'
                        onMouseEnter={onHover}
                        onMouseLeave={onHover}
                        primary="true"
                        dark="true">
                        Get started
                    </BtnLinkHome>
                    <BtnLinkSing to='/signin'
                        onMouseEnter={onHover}
                        onMouseLeave={onHover}
                        primary="true"
                        dark="true">
                        Log in
                    </BtnLinkSing>
                </HeroBtnWrapper>
            </HeroContent>
        </HeroContainer>
    )
}

export default HeroSection;
